import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import axios from 'axios';
import './index.js';

let id = Math.ceil(Math.random()*10000);

const App= (props) => {
     const [messages, setMessages] = useState([])
     const [input, setInput] = useState('');
     const [name, setName] = useState([])

useEffect (()=> {
  const subscribe =()=> axios.post('http://localhost:5200/subscribers',{id}).then((res)=>{
    setMessages((messages)=> messages.concat(res.data));
    subscribe();
  })
  subscribe()

},[]) //[] to call it once 

     const handleChange = (e) =>{
       const{target:{value}} = e
       setInput(value)
     }

     const handleSubmit = (e) => {
       e.preventDefault();
       axios.post('http://localhost:5200/messageSubscribers',{content: input}).then(()=> setInput(''))

       }   
      return(
         <div className="App">
      <header className="App-header">
      <form name="publish" id="form" onSubmit={handleSubmit} >
            <input id="content"  type="text" name="content" placeholder="Enter your message" onChange={handleChange} value={input.content} />
            <input id="username" type="text" name="name" placeholder="Enter your name" onChange={handleChange} value={input.name} />
            <input type="submit"  />
          </form>

          <div id="subscribe">
                  {messages.map(m => <h1 key={m.content}> {m.content} </h1>)}
          </div>
      </header>
    </div>
  );
     }


export default App;
